class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<int,int>mp;
        for(int i = 0;i<n;i++){
            if(nums[i] == k) ans++;
            unordered_map<int,int>mp1;
            mp1[nums[i]] = 1;
            for(auto &it: mp){
                int temp = it.first & nums[i];
                if(temp == k){
                    ans += it.second;
                }
                mp1[temp] += it.second;
            }
            mp = mp1;
        }

        return ans;
    }
};
