class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        map<int,int>dp;
        for(auto x:nums){
            map<int,int>temp;
            temp[x]++;
            for(auto [key,val]:dp){
              temp[key&x]+=val;
            }
            dp=temp;
            ans+=dp[k];
        }
        return ans;
    }
};
