class Solution:
    def countSubarrays(self, nums: List[int], target: int) -> int:
        n=len(nums)
        dp=[defaultdict(int) for i in range(n)]
        dp[0][nums[0]]=1

        for i in range(1,n):
            dp[i][nums[i]]=1
            for k,v in dp[i-1].items():
                dp[i][nums[i]&k]+=(v)


        #print(dp)

        op=0
        for i in range(n):
            for k,v in dp[i].items():
                if k==target:
                    op+=v
        return op
