class Solution {
public:

    typedef long long ll;

    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll count = 0;
        unordered_map<ll , ll> andCount;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll , ll> newAndCount;
            newAndCount[nums[i]]++;
            if (nums[i] == k) {
                count++;
            }

            for (auto pair : andCount) {
                ll newAnd = pair.first & nums[i];
                newAndCount[newAnd] += pair.second;
                if (newAnd == k) {
                    count += pair.second;
                }
            }

            andCount = newAndCount;
        }

        return count;
    }
};
