from typing import List
from collections import defaultdict

class Solution:
    def countSubarrays(self, a: List[int], t: int) -> int:
        n = len(a)
        total = 0
        p = defaultdict(int)

        for i in range(n):
            c = defaultdict(int)

            if a[i] == t:
                total += 1

            c[a[i]] = 1

            for v, f in p.items():
                r = v & a[i]
                if r == t:
                    total += f
                c[r] += f

            p = c

        return total
