class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long int ans = 0;
        unordered_map<long long int,long long int> mp1,mp2;
        for(int i : nums) {
            mp2.clear();
            mp2[i] = 1;
            for(auto& [v,f] : mp1) {
                int x = v & i;
                if(x == k)
                    ans += f;
                mp2[x] += f;
            }
            if(i == k)
                ans++;
            mp1 = mp2;
        }
        return ans;
    }
};
