class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0;
        unordered_map<int,int> pre;
        for(int i = 0 ; i < nums.size() ; i++){
            unordered_map<int, int> cur;
            for(auto &[v, cnt] : pre){
                int nea = v&nums[i];
                cur[nea] += cnt;
            }
            cur[nums[i]]++;
            if(cur.count(k)){
                res += cur[k];
            }
            pre = cur;
        }
        return res;
    }
};
