class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size();
        long long count = 0;
        std::unordered_map<long long, long long> andMap;

        for (long long i = 0; i < n; ++i) {
            std::unordered_map<long long, long long> newAndMap;
            newAndMap[nums[i]] = 1;

            for (auto& entry : andMap) {
                long long newAnd = entry.first & nums[i];
                newAndMap[newAnd] += entry.second;
            }

            andMap = newAndMap;

            if (andMap.find(k) != andMap.end()) {
                count += andMap[k];
            }
        }

        return count;
    }
};
