class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        and_map = {}

        for num in nums:
            new = {}
            if num == k: count += 1

            for and_val in and_map:
                new_val = and_val & num
                if new_val == k:
                    count += and_map[and_val]
                if new_val in new:
                    new[new_val] += and_map[and_val]
                else:
                    new[new_val] = and_map[and_val]

            if num in new: new[num] += 1
            else: new[num] = 1
            and_map = new

        return count
