class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
        long long arrSize = arr.size();
        long long resultCount = 0;
        typedef long long ll;
        unordered_map<long long, long long> prevResultsMap;
        for (int idx = 0; idx < arrSize; idx++) {
            unordered_map<ll, ll> currentResultsMap; 
            if (arr[idx] == target) {
                ++resultCount;
            }
            currentResultsMap[arr[idx]] = 1;
            for (auto &entry : prevResultsMap) {
                long long newAndResult = entry.first & arr[idx];
                if (newAndResult == target) {
                    resultCount += entry.second;
                }
                currentResultsMap[newAndResult] += entry.second;
            }
            prevResultsMap = currentResultsMap;
        }
        return resultCount;
    }
};
