#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        int size = nums.size(); 
        ll total = 0;
        unordered_map<ll, ll> previousResults;

        for (int index = 0; index < size; ++index) {
            unordered_map<ll, ll> currentResults;
            ll target = k; 

            if (nums[index] == target) {
                ++total;
            }
            currentResults[nums[index]] = 1;

            for (auto& [value, frequency] : previousResults) {
                ll newAndResult = value & nums[index]; 
                if (newAndResult == target) {
                    total += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }
    return total;
    }
};
