class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int n = array.size();
        long long ans = 0;
        unordered_map<long long, long long> prev;
        for (int index = 0; index < n; ++index) {
            unordered_map<long long, long long> cur; 
            if (array[index] == target) {
                ans++;
            }
            cur[array[index]] = 1;
            for (auto& [value, fre] :prev) {
                long long r = value & array[index];
                if (r == target) {
                    ans += fre;
                }
                cur[r] += fre;
            }

            prev = cur;
        }
        return ans;
    }
};
