class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<long long, long long> mp, np;
        long long ans = 0;
        for (int i = 0; i < n; i++) {
            np.clear();
            np[nums[i]]++;

            for (auto it : mp) {
                long long temp = it.first & nums[i];
                if (temp == k) {
                    ans += it.second;
                }
                np[temp] += it.second;
            }
            if (nums[i] == k)
                ans++;
            mp = np;
        }
        return ans;
    }

};
