class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        long long res = 0;
        vector<array<int,2>>v;
        for(int i = 0;i < n;i ++ ) {
            int x = a[i],lst = x,lpos = i;
            vector<array<int,2>>vv;
            v.push_back({x,i});
            // for(int j = v.size() - 1;i >= 0;i -- ) {
            //     auto &[val,idx] = v[j];
            //     if(val & x < k)break;
            //     if(val & x != lst)lst = 
            // }
            for(int j = v.size() - 1;j >= 0;j -- ) {
                auto &[val,idx] = v[j];
                val &= x;
                if(val != lst) {
                    vv.push_back({lst,lpos});
                    lst = val;lpos = idx;
                }
                lpos = idx;
                if(j == 0) {
                    vv.push_back({val,idx});
                }
            }
            reverse(vv.begin(),vv.end());
            v = vv;
            // int tmp = 0;
            for(int j = 0;j < v.size();j ++ ) {
                auto &[val,idx] = v[j];
                // cout << val << ' '  << idx << '\n';
                if(val == k) {
                    if(j < v.size() - 1) {
                        auto &[nval,nidx] = v[j + 1];
                        res += nidx - idx;
                        // cout << idx << ' ' << nidx << ' ' << "SDAS\n";
                    }
                    else  {
                        res += i - idx + 1;
                        // cout << i - idx + 1 << "ASDASD\n";
                    }
                }
            }
            // cout << tmp << '\n';
            // res += tmp;
        }
        // cout << "\n\n";
        return res;
    }
};
