class Solution {
public:
    long long countSubarrays(vector<int>& vec, int t) {
           int s = vec.size();

        long long rem = 0;

         unordered_map<long long, long long> kpp;


        for (int idx = 0; idx < s; ++idx) {

            unordered_map<long long, long long> curr; 

              if (vec[idx] == t) {
                ++rem;
              }

            curr[vec[idx]] = 1;
            for (auto& [value, fre] : kpp) {
                     long long res = value & vec[idx];
                if (res == t) {
                rem += fre;
                }
                curr[res] += fre;
            }

            kpp = curr;

        }
        return rem;
    }
};
