from collections import defaultdict
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n=len(nums)
        t=0
        d=defaultdict(int)
        for i in range(n):
            d1=defaultdict(int)
            if(nums[i]==k):
                t+=1
            d1[nums[i]]=1
            for j,m in d.items():
                new = j & nums[i]
                if(new==k):
                    t+=m
                d1[new]+=m

            d=d1

        return t
