class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0;
        unordered_map<long long, long long> cpp;
        for (int i = 0; i < n; ++i) 
        {
            unordered_map<long long, long long> mpp; 
            if (nums[i] == k) 
            {
                res++;
            }
            mpp[nums[i]] = 1;
            for (auto& [cnt, freq] : cpp) 
            {
                long long result = cnt & nums[i];
                mpp[result] += freq;
                if (result == k) 
                {
                    res += freq;
                }
            }
            cpp = mpp;
        }
        return res;
    }
};
