class Solution:
    def countSubarrays(self, nums: List[int], t: int) -> int:


        n = len(nums)
        ans = 0
        ors = []
        for i in range(n - 1, -1, -1):
            num = nums[i]
            ors.append([num, i, i])
            k = 0
            for p in ors:
                p[0] &= num
                if ors[k][0] == p[0]:
                    ors[k][1] = p[1]
                else:
                    k += 1
                    ors[k] = p
            del ors[k + 1:]
            for o, l, r in ors:
                if o == t:
                    ans += r - l + 1

        return ans
