class Solution {
public:

    void solve(int a,int b){
        if(a>=b) a=b;
        else b-=2;
    }
    long long ans=0;
    map<long long,long long>m;

    long long countSubarrays(vector<int>& nums, int k) {

        int n=nums.size();
        solve(0,1);
        for(int i=0;i<n;i++){
            map<long long,long long>helper;
            if(nums[i]==k) ans++;
        helper[nums[i]]=1;
        for(auto it:m){
            long long curr=it.first&nums[i];
            if(curr==k) ans+=it.second;
            helper[curr]+=it.second;
            solve(20,30);
        }
        m=helper;
        }
        return ans;
    }
};
