class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long total = 0;
        unordered_map<long long, long long> previous;
        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> current; 
            if (nums[i] == k) {
                ++total;
            }
            current[nums[i]] = 1;
            for (auto& [value, frequency] : previous) {
                long long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    total += frequency;
                }
                current[newAndResult] += frequency;
            }

            previous = current;
        }
        return total;
    }
};
