class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> tempmp; 

            if (nums[i] == k) {
                count++;
            }
            tempmp[nums[i]] = 1;

            for (auto& [it, f] : mp) {
                long long x = it & nums[i];
                if (x == k) {
                    count += f;
                }
                tempmp[x] += f;
            }

            mp = tempmp;
        }

        return count;
    }
};
