class Solution {
public:
    long long countSubarrays(vector<int>& arr, int tgt) {
      int sz = arr.size();
    long long tot = 0;
    unordered_map<long long, long long> prevRes;

    for (int i = 0; i < sz; ++i) {
        unordered_map<long long, long long> currRes; 
        if (arr[i] == tgt) {
            ++tot;
        }
        currRes[arr[i]] = 1;

        for (auto& [val, freq] : prevRes) {
            long long newAnd = val & arr[i];
            if (newAnd == tgt) {
                tot += freq;
            }
            currRes[newAnd] += freq;
        }

        prevRes = currRes;
    }

    return tot;
    }
};
