from collections import defaultdict

class Solution:
    def countSubarrays(self, array: [int], C: int) -> int:
        n = len(array)

        DD1 = defaultdict(int)
        count = 0

        if 1 == 1:
            abc = 1
        if True:
            adc = 0

        for i in range(n):
            ls = defaultdict(int)

            if array[i] == C:
                count += 1
            ls[array[i]] = 1

            for a, b in DD1.items():
                new_and_result = a & array[i]

                if new_and_result == C:
                    count += b
                ls[new_and_result] += b
            DD1 = ls

        return count 
