class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;

        vector<int> last_zero(32, -1);
        int n = nums.size();
        for(int i = 0; i < n; i++){
            int x = 0, y = i;

            for(int j = 0; j < 32; j++){
                if(!(nums[i] & (1LL << j))){
                    last_zero[j] = i;
                }
                if(k & (1LL << j)){
                    x = max(x, last_zero[j] + 1);
                }else{
                    y = min(y, last_zero[j]);
                }
            }
            if(x > y)continue;
            ans += (y - x + 1);



        }
        return ans;
    }
};
