from functools import cache
from math import inf
from typing import List


class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        def g(x):  
            cnt = [0] * 30
            i = 0
            while k:
                cnt[i] = k & 1
                x >>= 1
            return "".join(cnt)
        n = len(nums)
        @cache
        def f(idx ,now ):
            if idx == n:
                return int(now == k)
            if now < k:
                return 0
            ans = (1 if now == k else 0)
            ans += f(idx + 1,now & nums[idx])
            return ans
        ans = 0
        for i in range(n):
            ans += f(i + 1,nums[i])
        return ans
