class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
     int n = nums.size();
        long long myAns = 0;
        unordered_map<long long, long long> prev, curr;
        //map + sliding window 
        for (int i = 0; i < n; i++) {
            curr.clear(); 
            if (nums[i] == k) {
                ++myAns;
            }
            curr[nums[i]] = 1;

            for (auto& [value, freq] : prev) {
                long long x = value & nums[i];
                if (x == k) {
                    myAns += freq;
                }
                curr[x] += freq;
            }

            prev = curr;
        }

        return myAns;
    }
};
