class Solution {
public:
    typedef long long int ll;
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll result = 0;

        unordered_map<ll, ll> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> curr; 
            if (nums[i] == k) {
                result++;
            }
            curr[nums[i]] = 1;

            for (auto& ele : prev) {
                ll temp = ele.first & nums[i];
                if (temp == k) {
                    result += ele.second;
                }
                curr[temp] += ele.second;
            }            
            prev = curr;
        }     

        return result;
    }
};
