#include <vector>
#include <unordered_map>

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> mp, curr;

        for (int i = 0; i < n; ++i) {
            curr.clear();
            if (nums[i] == k) {
                ans++;
            }
            curr[nums[i]]++;

            for (auto& it : mp) {
                long long flag = it.first & nums[i];
                if (flag == k) {
                    ans += it.second;
                }
                curr[flag] += it.second;
            }
            mp.swap(curr);
        }
        return ans;
    }
};
