class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
          int n = arr.size();
        long long expected_ans = 0;

        unordered_map<long long, long long> map_frq_count;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> storage_buff; 

            if (arr[i] == k) {
                ++expected_ans;
            }
            storage_buff[arr[i]] = 1;

            for (auto& [val, freq] : map_frq_count) {
                long long response = val & arr[i];
                if (response == k) {
                    expected_ans += freq;
                }
                storage_buff[response] += freq;
            }

            map_frq_count = storage_buff;
        }

        return expected_ans;
    }
};
