#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll count = 0;
        unordered_map<ll, ll> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> mp1;

            if (nums[i] == k) {
                ++count;
            }
            mp1[nums[i]] = 1;

            for (auto it : mp) {
                ll res = it.first & nums[i];
                if (res == k) {
                    count += it.second;
                }
                mp1[res] += it.second;
            }

            mp = mp1;
        }

        return count;
    }
};
