class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long aryan = 0;
        unordered_map<long long, long long> unmap; //unordered map ko banaya
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> tempunmap;  //ussi ka temp map ko banaya
            if (k==nums[i]) {  //values ko check kiya
                aryan=aryan+1;
            }
            tempunmap[nums[i]] = 1; //1 ko dala initialize
            for (const auto& [val, freq] : unmap) {  //loop 2 chalayaa
                if (k==(val & nums[i])) {  // k ki value kodalaa
                    aryan = aryan+freq;
                }
            tempunmap[val & nums[i]] += freq; //temp ko dala freq me
            }
        unmap.swap(tempunmap); //alast me sab swap kiya
        }
        return aryan; //return karwaya ans ko
    }
};
