class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
       map<long long, long long> dp;
        long long ans = 0;
        int n = arr.size();
        vector<long long >nums(n);
         for(int i=0;i<n;i++){
            nums[i]=arr[i]*1LL;
         }
        for (int i = 0; i < n; i++) {
            map<long long, long long> mp; 
            if (nums[i] == k) {
                ans++;
            }
            mp[nums[i]] = 1;
            for (auto x: dp) {
                 long long v=x.first;
                 long long f=x.second;
                    v &=(nums[i]*1LL);
                if (v == k) {
                    ans += f;
                }
                mp[v] += f;
            }

           dp.clear();
           for(auto x:mp){
               dp.insert({x.first,x.second});
           }
        }
        return ans;

    }
};
