class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long> mp1;
        for (int i=0;i<n;i++) {
            unordered_map<long long, long long> mp2; 
            if (nums[i] == k) {
                cnt++;
            }
            mp2[nums[i]] = 1;
            for (auto& [key, freq] : mp1) {
                long long ans = key & nums[i];
                if (ans == k) {
                    cnt += freq;
                }
                mp2[ans] += freq;
            }

            mp1 = mp2;
        }
        return cnt;
    }
};
