class Solution {
public:
         long long countSubarrays(vector<int>& arr, int target) {
        int size = arr.size();
        long long validSubarraysCount = 0;

        unordered_map<long long, long long> previousResults;

        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults;

            if (arr[index] == target) {
                ++validSubarraysCount;
            }
            currentResults[arr[index]] = 1;

            for (auto& [andResult, frequency] : previousResults) {
                long long newAndResult = andResult & arr[index];
                if (newAndResult == target) {
                    validSubarraysCount += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }

        return validSubarraysCount;
    }
};
