#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;  
        unordered_map<ll, ll> prevMp;
        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> currMp; 

            if (nums[i] == k) {
                cnt++;
            }
            currMp[nums[i]] = 1;
            for (auto& [val, freq] : prevMp) {
                ll res = val & nums[i];
                if (res == k) {
                    cnt += freq;
                }
                currMp[res] += freq;
            }

            prevMp = currMp;
        }

        return cnt;
    }
};
