class Solution {
private:
    long long sol(vector<int>& nums, int k) {
        // solution for above problem
         long long n = nums.size();
        long long tar = k;
        long long ans = 0;
        map<long long, long long> prev;
        long long i = 0;
        while(i<n){
            map<long long, long long> cur; 
            if (nums[i] == k) {
                ans++;
            }
            cur[nums[i]] = 1;
            for (auto it : prev) {
                long long j1 = it.first;
                long long j2 = it.second;
                long long new1 = (j1 & nums[i]);
                if (new1 == k) {
                    ans += j2;
                }
                cur[new1] += j2;
            }
            prev = cur;
            i++;
        }
        return ans;
    }
public:
    long long countSubarrays(vector<int>& nums, int k) {
        return sol(nums,k);
    }
};
