class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        // no. of subarrays with bitwize AND = k
        int n = nums.size();
        long long ans=0;
        unordered_map<long long, long long>mp;
        for(int i=0; i<n; i++){
            unordered_map<long long, long long>temp;
            if(nums[i]==k) ans++;
            temp[nums[i]]=1;
            for(auto it:mp){
                long long AND = (it.first & nums[i]);
                if(AND == k)ans+=it.second;
                temp[AND]+=it.second;
            }
            mp=temp;
        }
        return ans;
    }
};
