class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0; map<long long, long long>first;
        for (int i = 0; i < nums.size(); ++i) {
            map<long long, long long> second;second[nums[i]] = 1;
            ans += (nums[i] == k) ? 1 : 0;
            for (auto it : first) {
                ans += ((it.first & nums[i]) == k) ? it.second : 0;
                second[(it.first & nums[i])] += it.second;
            }
            first = second;
        }

        return ans;
    }
};
