class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;

        map<long long, long long>mp;

        for(int i=0;i<n;i++){
            map<long long, long long>m;
            if(nums[i]==k)cnt++;
            m[nums[i]]=1;
            for(auto it=mp.begin();it!=mp.end();it++){
                long long resand=(it->first & nums[i]);
                if(resand==k)cnt+=(it->second);
                m[resand]+=(it->second);
            }
            mp=m;
        }

        return cnt;
    }
};
