class Solution {
public:
    long long countSubarrays(std::vector<int>& nums, int k){
        int n=nums.size();
        map<long long,long long>a;
        long long count=0;
        for (int i=n-1;i>=0;--i){
            map<long long,long long>b;
            for(auto it:a){
                b[it.first&nums[i]]+=it.second;
            }
            b[nums[i]]++;
            a=b;
            if(a.find(k)!=a.end()){
                count+=a[k];
            }
        }
        return count;
    }
};
