#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
        ll count = 0;
        unordered_map<ll,ll> mp;
        for (int i = 0; i < n; ++i) {
            unordered_map<ll,ll> mp2; 
            if (nums[i] == k) {
                ++count;
            }
            mp2[nums[i]]=1;
            for (auto ele : mp) {
                ll currAnd = ele.first&nums[i];
                 mp2[currAnd]+=ele.second;
                if (currAnd==k) {
                    count+=ele.second;
                }
            }
            mp = mp2;
        }
        return count;
    }
};
