class Solution {
public:
    long long countSubarrays(vector<int>& arr, int tg) {
        int sz=arr.size();
        long long ttl=0;
        unordered_map<long long,long long> pres;
        for(int i=0;i<sz;++i) {
            unordered_map<long long,long long> cur; 
            if(arr[i]==tg)++ttl;
            cur[arr[i]]=1;
            for(auto&[val,freq]:pres) {
                long long nw=val&arr[i];
                if(nw==tg)ttl+=freq;
                cur[nw]+=freq;
            }
            pres=cur;
        }
        return ttl;
    }
};
