class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<long long,long long> mp;
        long long ans = 0;

        for(int i = 0; i<n; i++) {
            unordered_map<long long, long long> mp2;
            if(nums[i] == k) ans++;
            mp2[nums[i]] = 1;
            for(auto& p:mp) {
                long long a = p.first;
                long long b = p.second;
                long long t = a & nums[i];
                if(t == k) ans += b;
                mp2[t] += b;
            }
            mp = mp2;
        }
        return ans;
    }
};
