class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,int>pre;
        pre[nums[0]] = 1;
        int n = nums.size();
        long long ans = nums[0] == k;
        for(int i = 1;i < n;i++){
            map<int,int>cur;
            for(auto [c,cnt] : pre){
                int num = c & nums[i];
                cur[num] += cnt;
            }
            cur[nums[i]]++;
            if(cur.count(k)){
                ans += cur[k];
            }
            pre = cur;

        }
        return ans;
    }
};
