from collections import defaultdict
from typing import List

class Solution:
    def countSubarrays(self, array: List[int], target: int) -> int:
        length = len(array)
        result_count = 0
        previous_map = defaultdict(int)

        for element in array:
            current_map = defaultdict(int)
            if element == target:
                result_count += 1
            current_map[element] = 1

            for value, frequency in previous_map.items():
                new_and_result = value & element
                if new_and_result == target:
                    result_count += frequency
                current_map[new_and_result] += frequency

            previous_map = current_map

        return result_count
