#include <vector>
using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> countMp;
        long long count = 0;
        int curr;

        for (int i = 0; i < nums.size(); ++i) {
            curr = nums[i];
            if (curr == k) count++;

            unordered_map<int, int> mp;
            mp[curr]++;

            for (auto& [prev_and, freq] : countMp) {
                int new_and = prev_and & curr;
                mp[new_and] += freq;

                if (new_and == k) {
                    count += freq;
                }
            }

            countMp = mp;
        }

        return count;
    }
};
