using ll =long long;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans = 0;
        map<ll,ll> mp1;
        for (auto &x:nums) {
            map<ll,ll> mp2; 
            mp2[x] = 1;
            for (auto &m : mp1) {
                if ((m.first & x) == k) ans += m.second;
                mp2[m.first & x] += m.second;
            }  
            ans+=(x==k);
            mp1 = mp2;
        }
        return ans;
    }
};
