class Solution {

public:

    long long countSubarrays(vector<int>& arr, int target) {

        int size = arr.size();

        long long total = 0;

        unordered_map<long long, long long> previousResult;

        for (int index = 0; index < size; ++index) {

            unordered_map<long long, long long> currentResult; 

            if (arr[index] == target) {

                ++total;

            }

            currentResult[arr[index]] = 1;

            for (auto& [value, frequency] : previousResult) {

                long long newAndResult = value & arr[index];

                if (newAndResult == target) {

                    total += frequency;

                }

                currentResult[newAndResult] += frequency;

            }



            previousResult = currentResult;

        }

        return total;

    }

};
