class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
          int n = nums.size();
        long long count = 0;

        // Track the AND results of subarrays ending at each index
        unordered_map<int, int> and_map;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> new_and_map;
            if (nums[i] == k) {
                ++count;
            }
            new_and_map[nums[i]] = 1;

            for (auto &pair : and_map) {
                int new_and = pair.first & nums[i];
                if (new_and == k) {
                    count += pair.second;
                }
                new_and_map[new_and] += pair.second;
            }

            and_map = new_and_map;
        }

        return count;
    }
};
