class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> mp1;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> mp2; 
            if (nums[i] == k) {
                ans++;
            }
            mp2[nums[i]] = 1;

            for (auto& val : mp1) {
                long long andans = val.first & nums[i];
                if (andans == k) {
                    ans += val.second;
                }
                mp2[andans] += val.second;
            }
            mp1 = mp2;
        }

        return ans;
    }
};
